import PetAvatar from './PetAvatar.js'
import {useForm} from 'react-hook-form'
import {savePet} from '../../services/Database'
import {db} from '../../services/firebase'
export default function PetForm(){
    const {register, handleSubmit} = useForm()

    const onSubmit = (values) =>{
        //console.log(values)
        //props.onAddAvatar()
        //savePet(values)
        /*
        console.log(values)
        console.log(otro)
        db.collection("pet").doc().set(otro)
        */
        const otro = {nombre: values.nombre, edad: values.edad}

        db.collection("gatos").doc().set(otro)
    }

    return(
        <form onSubmit={handleSubmit(onSubmit)}>
                <div>
                    <label>Nombre</label>
                    <input
                    ref={register} 
                    name="nombre"
                    type="text" 
                    className="form-control" 
                    id="inputNombre" 
                    placeholder="Evite"></input>
                </div>


                <div>
                    <label>Edad</label>
                    <input 
                    ref={register}
                    name="edad" 
                    type="number" 
                    className="form-control" 
                    id="inputEdad" 
                    placeholder="4"></input>
                </div>

                <div>
                    <PetAvatar
                    label={"avatar"}
                    register={register}> 
                    </PetAvatar>
                </div>

                <div >
                    <button 
                    type="submit" 
                    className="btn btn-primary" 
                    >Submit</button>
                </div>
            </form>
    )
}
