import { TextField, MenuItem, InputAdornment, Button} from '@material-ui/core'
import Grid from '@material-ui/core/Grid'

export default function MeetFirstStep({label, register}){


    const tipoConsultas=[
        "Vacuna",
        "Control",
        "Pelea con otra rata",
        "Depresion",
        "Celos"
    ]

    const profesionales = (consulta) => {
        if(consulta === "Vacuna"){
            return "Ivana"
        }
        else{
            return "German"
        }
    }

    return(
        <>
            <Grid container spacing={2} noValidate>
                <Grid item xs={12} sm={6}>
                    <TextField
                    name="consulta"
                    inputRef={(ref)=>{
                        if(!ref) return;
                        register({name:"tipo", value: ref.value})
                    }}
                    fullWidth
                    id="tipo"
                    select
                    label="Consulta"
                    helperText="Motivo de la consulta"
                    >
                    {tipoConsultas.map((tipo) => (
                        <MenuItem key={tipo} value={tipo}>
                            {tipo}
                        </MenuItem>
                    ))}
                    </TextField>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                    name="profesional"
                    inputRef={(ref)=>{
                        if(!ref) return;
                        register({name:"profesional", value: ref.value})
                    }}
                    fullWidth
                    id="profesional"
                    select
                    label="profesional"
                    helperText="Por favor selecciona el profesional"
                    >
                    {tipoConsultas.map((tipo) => (
                        <MenuItem key={tipo} value={tipo}>
                            {tipo}
                        </MenuItem>
                    ))}
                    </TextField>
                </Grid>

            </Grid>
        </>
    )
}
