import { TextField, MenuItem, InputAdornment, Button} from '@material-ui/core'
import Grid from '@material-ui/core/Grid'
import {useState} from 'react'
export default function MeetFirstStep({label, register}){

    const [profesional, setProfesional] = useState('')
    const [consulta, setConsulta] = useState('')
    const [disabled, setDisable] = useState(true)
    const tipoConsultas=[
        "Vacuna",
        "Control",
        "Pelea con otra rata",
        "Depresion",
        "Celos"
    ]

    const changeConsulta = (event) =>{
        const consulta = event.target.value
        setConsulta(consulta)
        setDisable(false)

        if(consulta === "Vacuna"){
            setProfesional(["Ivana"])
        }
        else{
            setProfesional(["German"])
        }
    }

    const changeProfesional = (event) => {

        const consulta = event.target.value
        console.log("Profesionales")
        console.log(consulta)
        if(consulta === "Vacuna"){
            return "Ivana"
        }
        else{
            return "German"
        }
    }

    return(
        <>
            <Grid container spacing={2} noValidate>
                <Grid item xs={12} sm={6}>
                    <TextField
                    name="consulta"
                    inputRef={(ref)=>{
                        if(!ref) return;
                        register({name:"tipo", value: ref.value})
                    }}
                    fullWidth
                    id="tipo"
                    select
                    label="Consulta"
                    helperText="Motivo de la consulta"
                    onChange={changeConsulta}
                    >
                    {tipoConsultas.map((tipo) => (
                        <MenuItem key={tipo} value={tipo}>
                            {tipo}
                        </MenuItem>
                    ))}
                    </TextField>
                </Grid>

                <Grid item xs={12} sm={6}>
                    <TextField
                    name="profesional"
                    inputRef={(ref)=>{
                        if(!ref) return;
                        register({name:"profesional", value: ref.value})
                    }}
                    fullWidth
                    id="profesional"
                    select
                    label="Profesional"
                    helperText="Por favor selecciona el profesional"
                    disabled={disabled}
                    >

                    {profesional!='' && profesional.map((tipo) => (
                        <MenuItem key={tipo} value={tipo}>
                            {tipo}
                        </MenuItem>
                    ))}
                    </TextField>
                </Grid>
            </Grid>
        </>
    )
}
