import React, { useState, Fragment } from 'react'
import Stepper from '@material-ui/core/Stepper'
import Step from '@material-ui/core/Step'
import StepLabel from '@material-ui/core/StepLabel'
import MeetFirstStep from './MeetFirstStep'
import MeetSecondStep from './MeetSecondStep'


const emailRegex = RegExp(/^[^@]+@[^@]+\.[^@]+$/)
const phoneRegex = RegExp(/^\D?(\d{3})\D?\D?(\d{3})\D?(\d{4,6})$/)
// Step titles

const labels = [ 'Motivo', 'Fecha y hora', 'Confirmacion' ]

export default function MeetForm(){
	const [ steps, setSteps ] = useState(0)
	const [ fields, setFields ] = useState({
        motivo: '',
        profesional:"",
		firstName: '',
		lastName: '',
		email: '',
		gender: '',
		date: '',
		city: '',
		phone: '',
		time: ''
	})
	// Copy fields as they all have the same name
	const [ filedError, setFieldError ] = useState({
		...fields
	})

	const [ isError, setIsError ] = useState(false)

	/*

	const handleNext = function(){
		setSteps(step )
	}

	*/ 


	// Proceed to next step
	const handleNext = () => setSteps(steps + 1)
	// Go back to prev step
	const handleBack = () => setSteps(steps - 1)

	// Handle fields change
	const handleChange = input => ({ target: { value } }) => {
		// Set values to the fields
		console.log("HOLA")

		setFields({
			...fields,
			[input]: value
		})

		// Handle errors
		const formErrors = { ...filedError }
		const lengthValidate = value.length > 0 && value.length < 3

		switch (input) {
			case 'firstName':
				formErrors.firstName = lengthValidate ? 'Minimum 3 characaters required' : ''
				break
			case 'lastName':
				formErrors.lastName = lengthValidate ? 'Minimum 3 characaters required' : ''
				break
			case 'email':
				formErrors.email = emailRegex.test(value) ? '' : 'Invalid email address'
				break
			case 'phone':
				formErrors.phone = phoneRegex.test(value) ? '' : 'Please enter a valid phone number. i.e: xxx-xxx-xxxx'
				break
			case 'city':
				formErrors.city = lengthValidate ? 'Minimum 3 characaters required' : ''
				break
			default:
				break
		}

		// set error hook
		Object.values(formErrors).forEach(error => (error.length > 0 ? setIsError(true) : setIsError(false)))
		// set errors hook
		setFieldError({
			...formErrors
		})

	}

	const handleSteps = step => {
		switch (step) {
			case 0:
				return (
					<MeetFirstStep
						handleNext={handleNext}
						handleChange={handleChange}
						values={fields}
						isError={isError}
						filedError={filedError}
					/>
				)
			case 1:
				return (
					<MeetSecondStep
						handleNext={handleNext}
						handleBack={handleBack}
						handleChange={handleChange}
						values={fields}
						isError={isError}
						filedError={filedError}
					/>
				)
			case 2:
				return <Confirm handleNext={handleNext} handleBack={handleBack} values={fields} />
			default:
				break
		}
	}

	/*
		<ul>
			<li key=1> Hola </li>
			<li> Otra cosa</li>
			<li> Otra cosa</li>
		<ul>
	*/ 

	// Handle components
	return (
		<Fragment>
			{steps === labels.length ? (
				<h1>Hola</h1>
			) : (
				<Fragment>
					<Stepper activeStep={steps} style={{ paddingTop: 30, paddingBottom: 50 }} alternativeLabel>
						{labels.map(label => (
							<Step key={label}>
								<StepLabel>{label}</StepLabel>
							</Step>
						))}
					</Stepper>
					{handleSteps(steps)}
				</Fragment>
			)}
		</Fragment>
	)
}
